import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Candidate } from 'src/app/model/candidate';
import { CandidateService } from 'src/app/service/candidate-service';

@Component({
  selector: 'app-candidate-list',
  templateUrl: './candidateListView.html',
  styleUrls: ['./candidateListView.css']
})
export class CandidateListView
{

  userRole:string = 'admin';
  allCandidate:Candidate[] | undefined;
  constructor(private candidateService:CandidateService,private router:Router){}
  ngOnInit(){
    this.candidateService.getAllCandidates().subscribe(data=>{
      this.allCandidate=data;
      console.log(this.allCandidate);
    })
  }
  showCandidateDetail(candidateId:string)
  {
    // window.location.href = this.userRole + "/check/candidate/detail/" + candidateId;
    this.router.navigate([`${this.userRole}/check/candidate/detail/${candidateId}`])
  }
  showAddNewAccountView() {

    this.router.navigate([`${this.userRole}/add/candidate/`])
    }
}
<div class="container mt-4 mb-4 pt-4" style="min-height: 800px;">
  <div class="row justify-content-center">
    <div class="col-12">
      <!-- <div class="card cardBackGround border border-success ">
        <label class="text-white">This is just a search bar</label>
      </div> -->
    </div>
    <div class="col-12 mt-2">
      <div class="card cardBackGround border border-success ">
        <div class="row text-white text_font text_middle text-center m-2 border-bottom border-success">
          <div class="col-2">Id</div>
          <div class="col-3">Candidate</div>
          <div class="col-3">Recruiter</div>
          <div class="col-2">Experience</div>
          <div class="col-1">Visa</div>
          <div class="col-1">
            <i class="bi bi-person-add" (click)="showAddNewAccountView()"></i>
          </div>
        </div>
        <div class="text-white text_font text_table text-center m-2" style="height: 400px; overflow: auto">
          <div *ngFor="let candidate of allCandidate">
            <div class="row py-0 border-bottom border-secondary" (click)="showCandidateDetail(candidate.candidateId)">
              <div class="col-2">{{candidate.candidateId}}</div>
              <div class="col-3">{{candidate.firstName + "  "+candidate.middleName+" "+candidate.lastName|stringCutOff:12}}</div>
              <div class="col-3">{{candidate.recruiterName|stringCutOff:9}}</div>
              <div class="col-2">{{candidate.workExperience}}</div>
              <div class="col-1">{{candidate.visaStatus}}</div>
              <div class="col-1">
                <i class="bi bi-search" ></i>
              </div>
            </div>
          </div>

          </div>
        <div class="row m-2 text-white text_font justify-content-end border-top border-success">
          Page:1
        </div>
      </div>
    </div>
  </div>
</div>
