import {Component, Input} from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent
{
  constructor(private router:Router){}
  @Input() title:any;

  @Input() userID:any;
  @Input() userName: any;
  @Input() userRole: any;

  fullName:string = "User Name";
  logout()
  {
    window.sessionStorage.removeItem("SNVA_CRM_USER");
    window.location.href = "login";
  }

  myAccount()
  {
    //window.location.href = this.userRole + "/check/user/detail/" + this.userID;
    this.router.navigate(["superAdmin/check/user/detail/1"])
  }
  accountlist()
  {
    this.router.navigate(["superAdmin/check"])
  }
}
<nav class="navbar navbar-expand-lg navbar-light text-white cardBackGround border-bottom border-success">
<div class="container-fluid justify-content-between">
  <div class="collapse navbar-collapse " id="navbar">
    <label class=" title" routerLink="">
      {{title}}
    </label>
    <ul class="navbar-nav mb-2 mb-lg-0">
      <li class="nav-item dropdown " style="caret-color: black;" >
        <a class="nav-link dropdown-toggle ms-4 text-white text_middle text_font" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          ManageTools
        </a>
        <div class="dropdown-menu ps-2 pe-2 bg-dark text-white border-success" aria-labelledby="navbarSuperDropdownMenuLink">
          <a class="dropdown-item text_font text-white">Add New User</a>
          <a class="dropdown-item text_font text-white">Manage Users</a>
          <a class="dropdown-item text_font text-white">Manage Candidate</a>
        </div>
      </li>
      <li class="nav-item dropdown " style="caret-color: black;" >
        <a class="nav-link dropdown-toggle ms-4 text-white text_middle text_font" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          QuickLink
        </a>
        <div class="dropdown-menu ps-2 pe-2 bg-dark text-white border-success" aria-labelledby="navbarSuperDropdownMenuLink">
          <a class="dropdown-item text_font text-white">MyDashboard</a>
          <a class="dropdown-item text_font text-white">MyAccount</a>
        </div>
      </li>
    </ul>
    <span class="nav-item dropdown ms-auto me-4" style="caret-color: black;" >
      <a class="nav-link dropdown-toggle ms-4 text-white text_middle text_font" style="color: white;"   href="#" id="navbarDropdownMenuLink" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            <i class="bi bi-person-circle"></i> {{userName}}
      </a>
      <div class="dropdown-menu ps-2 pe-2 bg-dark text-white border-success" aria-labelledby="navbarDropdownMenuLink">
        <a class="dropdown-item text_font text-white" (click)="myAccount()">My Account</a>
        <a class="dropdown-item text_font text-white" routerLink="">Message</a>
        <a class="dropdown-item text_font text-white" (click)="logout()">Log Out</a>
      </div>
    </span>
  </div>
</div>
</nav>
