import { Component } from '@angular/core';
import {BsModalRef, BsModalService} from 'ngx-bootstrap/modal';

@Component({
  selector: 'app-confirm-view',
  templateUrl: './confirm-view.component.html',
  styleUrls: ['./confirm-view.component.css']
})
export class ConfirmViewComponent
{
  message:string = 'reply message';
  error:string = 'error';
  url:string = '';

  constructor(public bsModalRef: BsModalRef)
  {
  }
}
<div class="card text_font border-success bg-dark">
  <label class="text_middle text-white m-2">{{message}}</label>
  <label class="text_error m-2">{{error}}</label>
  <div class="row m-2 justify-content-center">
    <div class="col-12">
      <div class="progress">
        <div class="progress-bar" style="width:100%;"></div>
      </div>
    </div>
  </div>
</div>
