import {Component, OnInit} from '@angular/core';

import type { Container, Engine, ISourceOptions } from "tsparticles-engine";
import { loadFull } from "tsparticles";
import configs from "tsparticles-demo-configs";

import { User } from './model/user'


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent
{
  title = 'SNVA CRM';
  user:User = new User();
  isLogin:boolean = true;

  userID:number = 1;
  userName:string = '';
  userRole:string = 'superAdmin';

  //particlesOptions: ISourceOptions = configs.basic;
  // configs.backgroundMask // good
  // configs.collisionsAbsorb // good
  particlesOptions: ISourceOptions =
    {
      background: { color: { value: "#000", }, },
      fpsLimit: 30,
      interactivity:
        {
          events:
            {
              onClick:
                {
                  enable: true,
                  mode: ["repulse"],
                },
              onHover:
                {
                  enable: true,
                  mode: ["attract"],
                },
              resize: true,
            },
          modes:
            {
              repulse:
                {
                  distance: 200,
                  duration: 0.4,
                  speed:1
                },
              attract:
                {
                  distance: 200,
                  duration: 0.4,
                  speed:2
                }
            },
        },
      particles:
        {
          color: { value: "#4ff",},
          links:
            {
              color: "#4ff",
              distance: 200,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
          collisions: { enable: false,},
          move:
            {
              direction: "none",
              enable: true,
              outModes: { default: "bounce",},
              random: true,
              speed: 1,
              straight: true,
            },
          number:
            {
              density:
                {
                  enable: true,
                  area: 800,
                },
              value: 100,
            },
          opacity: { value: 0.5, },
          shape: { type:  "circle", },
          size: { value: { min: 1, max: 5}, },
        },
      detectRetina: true,
    }

  constructor()
  {
    // @ts-ignore
    this.user = JSON.parse( window.sessionStorage.getItem('SNVA_CRM_USER') );
    console.log(this.user);
    if(this.user == null)
    {

      this.user = new User();
      //this.isLogin = false;
      this.user.userId = 1;
      console.log("new user");
    }
    else
    {
      console.log(this.user);
      this.userName = this.user.username;
      this.userRole = this.user.role;
      this.userID   = this.user.userId;
      this.isLogin = true;
    }
  }

  async particlesInit(engine: Engine): Promise<void>
  {
    console.log("init", engine);
    await loadFull(engine);
  }

  public particlesLoaded(container: Container): void {
    console.log("loaded", container);
  }

}
<!-- Background -->
<ng-particles
  id="tsparticles"
  [options]="particlesOptions"
  [particlesInit]="particlesInit"
  (particlesLoaded)="particlesLoaded($event)"
>
</ng-particles>

<!-- after login -->
<div *ngIf="isLogin==true">
  <app-header [title] = 'title' [userID] = 'userID' [userName] = 'userName' [userRole] = 'userRole'></app-header>
  <div style="min-height: 800px">
    <router-outlet></router-outlet>
  </div>
  <app-footer></app-footer>
</div>

<!-- before login -->
<div *ngIf="isLogin==false">
  <app-log-in></app-log-in>
</div>
